	},
	{
		name: 'Role',
		value: () => falso.randRole(),
		description: 'The role or position held by the randomly generated user.',
	},
	{
		name: 'Company',
		value: () => falso.randCompanyName(),
		description:
			'The name of the company associated with the randomly generated user.',
	},
	{
