        const [foreground, accent, ui, background, opacity] = value
        theme.foreground = foreground
        theme.accent = accent
        theme.ui = ui
        theme.background = background
        theme.opacity = opacity

        setTheme(theme);
        writeJson(themePath, theme)
    },
    fields: arrayList,
})

let result = await div(md(`# Success!`))
